import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-add-track-dialog',
  template: `
    <div class="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" *ngIf="isOpen">
      <div class="bg-surface p-6 rounded-lg w-full max-w-md mx-4">
        <h2 class="text-xl font-bold mb-4">Ajouter une piste</h2>

        <form [formGroup]="trackForm" (ngSubmit)="onSubmit()">
          <!-- File Upload -->
          <div class="mb-4">
            <label class="block text-sm font-medium mb-2">Fichier audio</label>
            <div 
              class="border-2 border-dashed border-gray-600 rounded-lg p-8 text-center cursor-pointer hover:border-primary transition-colors"
              (click)="fileInput.click()"
              [class.border-primary]="selectedFile">
              <input
                #fileInput
                type="file"
                class="hidden"
                accept=".mp3,.wav,.ogg"
                (change)="onFileSelected($event)"
              >
              <div class="text-gray-400">
                {{ selectedFile ? selectedFile.name : 'Cliquez pour sélectionner un fichier audio' }}
              </div>
            </div>
          </div>

          <!-- Title -->
          <div class="mb-4">
            <label class="block text-sm font-medium mb-2">Titre</label>
            <input
              type="text"
              formControlName="title"
              class="w-full p-2 rounded bg-gray-800 border border-gray-700 focus:border-primary focus:outline-none"
              placeholder="Nom de la piste"
            >
          </div>

          <!-- Artist -->
          <div class="mb-4">
            <label class="block text-sm font-medium mb-2">Artiste</label>
            <input
              type="text"
              formControlName="artist"
              class="w-full p-2 rounded bg-gray-800 border border-gray-700 focus:border-primary focus:outline-none"
              placeholder="Nom de l'artiste"
            >
          </div>

          <!-- Category -->
          <div class="mb-4">
            <label class="block text-sm font-medium mb-2">Catégorie</label>
            <select
              formControlName="category"
              class="w-full p-2 rounded bg-gray-800 border border-gray-700 focus:border-primary focus:outline-none"
            >
              <option value="">Sélectionnez une catégorie</option>
              <option value="pop">Pop</option>
              <option value="rock">Rock</option>
              <option value="rap">Rap</option>
              <option value="cha3bi">Cha3bi</option>
              <option value="other">Autre</option>
            </select>
          </div>

          <!-- Description -->
          <div class="mb-6">
            <label class="block text-sm font-medium mb-2">Description (optionnel)</label>
            <textarea
              formControlName="description"
              class="w-full p-2 rounded bg-gray-800 border border-gray-700 focus:border-primary focus:outline-none"
              rows="3"
              placeholder="Description de la piste"
            ></textarea>
          </div>

          <!-- Buttons -->
          <div class="flex justify-end space-x-3">
            <button
              type="button"
              class="px-4 py-2 rounded text-gray-400 hover:text-white"
              (click)="close()"
            >
              Annuler
            </button>
            <button
              type="submit"
              class="btn-primary"
              [disabled]="!trackForm.valid || !selectedFile"
            >
              Ajouter
            </button>
          </div>
        </form>
      </div>
    </div>
  `
})
export class AddTrackDialogComponent {
  isOpen = false;
  trackForm: FormGroup;
  selectedFile: File | null = null;

  constructor(private fb: FormBuilder) {
    this.trackForm = this.fb.group({
      title: ['', [Validators.required, Validators.maxLength(50)]],
      artist: ['', [Validators.required]],
      category: ['', [Validators.required]],
      description: ['', [Validators.maxLength(200)]]
    });
  }

  onFileSelected(event: Event) {
    const input = event.target as HTMLInputElement;
    if (input.files?.length) {
      const file = input.files[0];
      if (file.size <= 15 * 1024 * 1024) { // 15MB max
        this.selectedFile = file;
      } else {
        alert('Le fichier est trop volumineux. Taille maximum : 15MB');
      }
    }
  }

  open() {
    this.isOpen = true;
  }

  close() {
    this.isOpen = false;
    this.trackForm.reset();
    this.selectedFile = null;
  }

  onSubmit() {
    if (this.trackForm.valid && this.selectedFile) {
      // TODO: Implement track upload logic
      console.log('Form Data:', this.trackForm.value);
      console.log('File:', this.selectedFile);
      this.close();
    }
  }
}
