import { Component, OnInit } from '@angular/core';
import { TrackService } from '../../../../core/services/track.service';
import { Track } from '../../../../core/models/track.model';
import { Observable } from 'rxjs';
import { PlayerService } from '../../../../core/services/player.service';

@Component({
  selector: 'app-library',
  template: `
    <div class="container mx-auto p-4">
      <!-- Search Bar -->
      <div class="mb-6">
        <input 
          type="text" 
          placeholder="Search tracks..."
          class="w-full p-3 rounded-lg bg-surface text-text border border-gray-700 focus:outline-none focus:border-primary"
          (input)="onSearch($event)"
        >
      </div>

      <!-- Tracks Grid -->
      <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
        <!-- Empty State -->
        <div *ngIf="(tracks$ | async)?.length === 0" class="col-span-full text-center py-10">
          <p class="text-gray-400 mb-4">No tracks added yet</p>
          <button class="btn-primary" (click)="openAddTrackDialog()">
            Add Track
          </button>
        </div>

        <!-- Track Cards -->
        <div *ngFor="let track of tracks$ | async" 
          class="bg-surface p-4 rounded-lg hover:bg-opacity-80 transition-colors cursor-pointer"
          (click)="playTrack(track)">
          <div class="aspect-square bg-gray-700 rounded-lg mb-3">
            <img 
              [src]="track.coverImage || 'assets/images/placeholder.png'" 
              [alt]="track.title"
              class="w-full h-full object-cover rounded-lg"
            >
          </div>
          <h3 class="font-semibold truncate">{{ track.title }}</h3>
          <p class="text-sm text-gray-400 truncate">{{ track.artist }}</p>
        </div>
      </div>

      <!-- Add Track Button -->
      <app-add-track-button></app-add-track-button>
    </div>
  `
})
export class LibraryComponent implements OnInit {
  tracks$: Observable<Track[]>;

  constructor(
    private trackService: TrackService,
    private playerService: PlayerService
  ) {
    this.tracks$ = this.trackService.tracks$;
  }

  ngOnInit() {
    this.trackService.loadTracks();
  }

  onSearch(event: Event) {
    const searchTerm = (event.target as HTMLInputElement).value;
    // TODO: Implement search functionality
  }

  async playTrack(track: Track) {
    try {
      await this.playerService.playTrack(track);
    } catch (error) {
      console.error('Error playing track:', error);
      alert('Failed to play track. Please try again.');
    }
  }

  openAddTrackDialog() {
    // This is handled by the AddTrackButton component
  }
}
