import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { Store } from '@ngrx/store';
import { Observable } from 'rxjs';
import { Track } from '../../../../core/models/track.model';
import * as TrackActions from '../../../../core/store/track/track.actions';
import * as fromTrack from '../../../../core/store/track/track.selectors';

@Component({
  selector: 'app-track-details',
  templateUrl: './track-details.component.html' 
})
export class TrackDetailsComponent implements OnInit {
  track$: Observable<Track | null>;
  deleting: boolean = false;

  constructor(
    private store: Store,
    private route: ActivatedRoute,
    private router: Router
  ) {
    this.track$ = this.store.select(fromTrack.selectCurrentTrack);
  }

  ngOnInit() {
    this.route.params.subscribe(params => {
      const trackId = Number(params['id']);
      this.store.dispatch(TrackActions.loadTrack({ id: trackId }));
    });
  }

  playTrack(track: Track) {
    this.store.dispatch(TrackActions.playTrack({ track }));
  }

  goBack() {
    this.router.navigate(['/library']);
  }

  deleteTrack() {
    this.deleting = true;
  }
  closeAlert(touched: boolean){
    if (touched) {
      this.deleting = false;
    }
  }

  confirmDeletion(confirmed: boolean, trackId: number) {
    if (confirmed) {
      this.store.dispatch(TrackActions.deleteTrack({ trackId }))
      rou
    }
  }

  editTrack(trackId: number) {

  }
} 
<div class="container mx-auto p-4" *ngIf="track$ | async as track">
  <div class="max-w-4xl mx-auto">
    <!-- Back Button -->
    <button 
      class="mb-6 flex items-center text-gray-400 hover:text-white"
      (click)="goBack()"
    >
      <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M10 19l-7-7m0 0l7-7m-7 7h18" />
      </svg>
      Back to Library
    </button>

    <!-- Track Info -->
    <div class="bg-surface rounded-lg p-6">
      <div class="flex flex-col md:flex-row gap-8">
        <!-- Cover Image -->
        <div class="w-full relative md:w-64 h-64 bg-gray-700 rounded-lg flex-shrink-0">
          <img 
            [src]="track.coverImage || 'assets/images/placeholder.png'" 
            [alt]="track.title"
            class="absolute w-full h-full object-cover rounded-lg"
          >

          <button 
            class="absolute shadow-2xl bottom-0 bg-gray-300 border border-red-500 hover:bg-red-500 hover:text-white trans text-red-500 p-2 m-2 hover:shadow-2xl rounded-full hover:opacity-90 transition ease-in-out delay-130 hover:-translate-y-1 hover:scale-100"
            (click)="deleteTrack()"
          >
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="size-6">
              <path stroke-linecap="round" stroke-linejoin="round" d="m14.74 9-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 0 1-2.244 2.077H8.084a2.25 2.25 0 0 1-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 0 0-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 0 1 3.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 0 0-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 0 0-7.5 0" />
            </svg>
          </button>

          <button
            class="absolute bottom-0 left-14 bg-gray-300 border border-blue-500 text-blue-500 hover:bg-blue-500 hover:text-white p-2 m-2 rounded-full hover:opacity-90 transition ease-in-out delay-130 hover:-translate-y-1 hover:scale-100"
            (click)="editTrack(track.id)"
          >
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="size-6">
              <path stroke-linecap="round" stroke-linejoin="round" d="m16.862 4.487 1.687-1.688a1.875 1.875 0 1 1 2.652 2.652L10.582 16.07a4.5 4.5 0 0 1-1.897 1.13L6 18l.8-2.685a4.5 4.5 0 0 1 1.13-1.897l8.932-8.931Zm0 0L19.5 7.125M18 14v4.75A2.25 2.25 0 0 1 15.75 21H5.25A2.25 2.25 0 0 1 3 18.75V8.25A2.25 2.25 0 0 1 5.25 6H10" />
            </svg>
          </button>
        </div>

        <!-- Track Details -->
        <div class="flex-1">
          <div class="flex justify-between items-start">
            <div>
              <h1 class="text-3xl font-bold mb-2">{{ track.title }}</h1>
              <p class="text-xl text-gray-400 mb-4">{{ track.artist }}</p>
            </div>
            <button 
              class="bg-primary text-white p-3 rounded-full hover:opacity-90"
              (click)="playTrack(track)"
            >
              <svg xmlns="http://www.w3.org/2000/svg" class="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z" />
              </svg>
            </button>
          </div>

          <div class="mt-6 space-y-4">
            <div>
              <h3 class="text-gray-400 text-sm">Category</h3>
              <p class="text-lg">{{ track.category }}</p>
            </div>
            <div>
              <h3 class="text-gray-400 text-sm">Added</h3>
              <p class="text-lg">{{ track.addedDate | date:'medium' }}</p>
            </div>
            <div>
              <h3 class="text-gray-400 text-sm">Description</h3>
              <p class="text-lg">{{ track.description || 'No description available.' }}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

  <app-confirmation-alert *ngIf="deleting" (confirmed)="confirmDeletion($event, track.id)" (touched)="closeAlert($event)"></app-confirmation-alert>
</div>
